import ast

def convert_to_gpx(coordinate_offset, input):
    lines = ast.literal_eval(input)

    output = ""

    output += """
<?xml version="1.0" encoding="UTF-8"?>
<gpx version="1.1" xmlns="http://www.topografix.com/GPX/1/1" 
     xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance" 
     xsi:schemaLocation="http://www.topografix.com/GPX/1/1 
                         http://www.topografix.com/GPX/1/1/gpx.xsd">
"""

    for line in lines:
        output += "  <trk>\n    <trkseg>\n"
        for coordinate in line:
            output += "      <trkpt lat=\"" + str((float(coordinate[0]) + coordinate_offset)) + "\" lon=\"" + str((float(coordinate[1]) + coordinate_offset)) + "\"/>\n"
        output += "    </trkseg>\n  </trk>\n"

    output += "</gpx>"
    
    with open("output.gpx", "w") as file:
        file.write(output)
    print("success!")
        
